mapper.select:
SELECT p.* FROM test_paper p LEFT JOIN subject s ON s.id = p.subject_id
p.title LIKE CONCAT("%",#{title},"%")
AND p.subject_id=#{subjectId}
AND p.status=#{status}
AND p.creator LIKE CONCAT("%",#{creator},"%")
AND DATE_FORMAT(p.create_time,'%Y-%m-%d') LIKE DATE_FORMAT(#{createTime},'%Y-%m-%d')

SELECT p.id, p.title, p.creator, s.`name` AS subject , r.mark, r.submit_count FROM test_paper_student_ref r LEFT JOIN test_paper p ON p.id = r.test_paper_id LEFT JOIN `subject` s ON s.id = p.subject_id
r.staff_id = #{staffId}
AND p.title LIKE CONCAT("%",#{title},"%")
AND p.subject_id=#{subjectId}
